}

.container {
  width: 100%;
  padding: 0 16px;
}

@media only screen and (min-width: 990px) {
  .container {
    padding: 0 53px;
  }
}
.nav {
  width: 100%;
  position: sticky;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.nav__logo {
  font-size: 24px;
  margin: 40px 0;
}
.nav__logo span {
  color: #C0239E;
}
.nav__links {
  display: none;
  list-style-type: none;
  margin: 0;
  padding: 0;
}
.nav__links li {
  font-size: 18px;
  margin-left: 64px;
