}
.nav__links li:last-child {
  margin-right: 64px;
}
.nav__links li:hover {
  color: #C0239E;
}

@media only screen and (min-width: 660px) {
  .nav__links {
    display: flex;
  }
}
.presentation {
  display: grid;
  grid-template-columns: 1fr;
  grid-template-areas: "body" "links";
  justify-content: center;
}
.presentation__body {
  grid-area: body;
  justify-self: center;
}
.presentation__desc {
  font-size: 14px;
  line-height: 133.8%;
  color: rgba(0, 0, 0, 0.53);
  margin: 32px 0;
}
.presentation__header {
  font-size: 26px;
  line-height: 36px;
}
.presentation img {
  display: none;
  grid-area: img;
  width: 100%;
  min-width: 200px;
  max-width: 320px;
}
.presentation__links {
  grid-area: links;
  justify-self: end;
  margin-top: 32px;
  margin-right: 32px;
}
.presentation__btn {
  width: 114px;
  height: 52px;
  cursor: pointer;
  outline: none;
  border: none;
  border-radius: 9px;
  color: white;
  position: relative;
  background: #7F00FF;
  background: -webkit-linear-gradient(to left, #E100FF, #7F00FF);
  background: linear-gradient(to left, #E100FF, #7F00FF);
  z-index: 0;
}
.presentation__btn::before {
  content: "";
  z-index: -1;
  background: white;
  border-radius: 9px;
  top: -1px;
  left: -2px;
  position: absolute;
  width: calc(100% + 2px);
  height: calc(100% + 2px);
  transform: scaleX(0);
  transition: transform 500ms;
  box-shadow: 0 0 1px 0 #7F00FF inset, 0 0 1px 0 #7F00FF;
  transform-origin: 0 0;
  transition-timing-function: cubic-bezier(0.51, 1.58, 0.41, 0.7);
}
.presentation__btn:hover::before {
  transform: scaleX(1);
  border: 1px solid #7F00FF;
}
.presentation__btn:hover {
  color: rgba(0, 0, 0, 0.87);
  font-weight: bold;
}
.presentation .presentation--pink {
  color: #C0239E;
}

